import React, { Component } from "react";
import Head from "next/head";


import Nav from "../nav";

const Layout = props => {

  return (
    <div className="ui container">
      <Head>
        <title>LOMA</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <Nav />
      {props.children}
    </div>
  );

}

export default Layout;
